import React from "react";
import "./Ad-Section.css";
import cmsImg from "../../assets/img/illustration.png";
import { Container, Row, Col } from "reactstrap";
import {constants} from "../../Const"
export const Ad = (props) =>  {
  if(props.page){
    var image ="";
    if(props.page.A_cardImage && props.page.A_cardImage.data && props.page.A_cardImage.data.length>0){
     image = constants.link+props.page.A_cardImage.data[0].attributes.url ;
    }
  return (
    <section className="Ad">
      <Container>
        <Row>
        <Col lg="6" md="6">
            <div className="Ad__content">
              <h2>Loved by developers, used by thousands around the world!</h2>
              <p>
              One of the biggest benefits of using Umbraco is that we have the friendliest Open Source community on this planet. A community that's incredibly pro-active, extremely talented and helpful.

If you get an idea for something you would like to build in Umbraco, chances are that someone has already built it. And if you have a question, are looking for documentation or need friendly advice, go ahead and ask the Umbraco community on Our.
              </p>
              <button>the heartcore</button>
            </div>
          </Col>
          <Col Lg="6" md="6" className="section">
          <Col size={9} md={4} >
                  <div className='box'>
                    <h1 className="CourseName">Umbraco Deploy</h1>
                   
                     <button>Explore Deploy</button>
                  </div>
                  </Col>
                  <Col size={9} md={4}>
                  <div className='box'>
                    <h1 className="CourseName">Umbraco Forms</h1>
                    
<button>Mangopulse Form Builder </button>
                  </div>
                  </Col>
                  <Col size={9} md={4}>
                  <div className='box'>
                    <h1 className="CourseName">Umbraco Learning Base</h1>
                  
                    <button>Press play and learn </button>
                  </div>
                  </Col>
          </Col>

       
        </Row>
        
      </Container>
    </section>
  );
  }
};
