import React from "react"
import "./features.css"
import { online } from "../../dummydata"
import Heading from "../common/heading/Heading"
import {componants} from "../../Const" ; 
export const Features = () => {
  var video ="";
  if(props.page.demo_media && props.page.demo_media.data && props.page.demo_media.data.length>0){
   video = constants.link+props.page.demo_media.data[0].attributes.url ;
  }
  return (
   
    <>
      <section className='online'>
        <div className='container'>
          <Heading subtitle='Plenty out of the box features' title='Open source! However, mixcore got everythings that you needs for your website / application!' />
          <div className='content grid3'>
            {online.map((val) => (
              <div className='box'>
                <div className='img'>
                  <img src="./images/security (2).png" />
                  <img src={val.hoverCover} alt='' className='show' />
                </div>
                <h1 className="CourseName">{val.courseName}</h1>
                <p id="textCourse">{val.course}</p>
              </div>
            ))}
          </div>
        </div>
      </section>
    </>
  )
}
