import React from 'react';
import CardItem from './CardItem';
import gif1 from "../assets/videos/cms-development.gif";
import gif2 from "../assets/videos/CMS-Website-Bgrafio.gif";
import gif3 from "../assets/videos/giphy.gif";
export const Cards = (props) => {
  if(props.page){
  return (

    <div className='cards'>
      <h1>{props.page.Title} </h1>
         <div className="cards__container">
            <div className="cards__wrapper">
                <ul className="cards__items">
                    <CardItem 
                    src={gif1}
                   
                    text={props.page.card1description}
                    label={props.page.card1button}
                    path='/services'
                    />
                     <CardItem
              src={gif2}
              text={props.page.card2button}
              label={props.page.card2description}
              path='/services'
            />
              <CardItem
              src={gif3}
              text={props.page.card3button}
              label={props.page.card3description}
              path='/sign-up'
            />
                </ul>
            </div>
         </div>
    </div>
  )
  }
}
