import React, { useContext } from "react";
import "./Intro.css";
import Vector1 from "../../img/Vector1.png";
import Vector2 from "../../img/Vector2.png";
import boy from "../../img/boy.svg";
import glassesimoji from "../../img/glassesimoji.png";
import thumbup from "../../img/cursor-ico.svg";
import crown from "../../img/point-ico.svg";
import star from "../../img/star-ico.svg";
import FloatinDiv from "../FloatingDiv/FloatingDiv";
import Github from "../../img/github.png";
import LinkedIn from "../../img/linkedin.png";
import Instagram from "../../img/instagram.png";

import { motion } from "framer-motion";
import { Link } from "react-scroll";
const Intro = () => {
  // Transition
  const transition = { duration: 2, type: "spring" };

  // context


  return (
    <div className="Intro" id="Intro">
      {/* left name side */}
      <div className="i-left">
        <div className="i-name">
          {/* yahan change hy darkmode ka */}


          <span>



On average our customers have seen a...
          </span>
        </div>
        <Link to="contact" smooth={true} spy={true}>

        </Link>
        {/* social icons */}
        <div className="i-icons">

        </div>
      </div>
      {/* right image side */}
      <div className="i-right">
        <img src={Vector1} alt="" />
        <img src={Vector2} alt="" />
        <img clasName="boy" src={boy} alt="" />
        {/* animation */}

        <motion.div
          initial={{ top: "-4%", left: "74%" }}
          whileInView={{ left: "60%" }}
          transition={transition}
          className="floating-div"
        >
          <FloatinDiv className="crown" img={crown}  />
        </motion.div>
 <motion.div
          initial={{ top: "10%", left: "74%" }}
          whileInView={{ left: "20%" }}
          transition={transition}
          className="floating-div"
        >
          <FloatinDiv className="star" img={star}  />

        </motion.div>
        {/* animation */}
        <motion.div
          initial={{ left: "9rem", top: "16rem" }}
          whileInView={{ left: "-2rem" }}
          transition={transition}
          className="floating-div"
        >
          {/* floatinDiv mein change hy dark mode ka */}
          <FloatinDiv className="thumbup" img={thumbup} />

        </motion.div>

        <div className="blur" style={{ background: "rgb(238 210 255)" }}></div>
        <div
          className="blur"
          style={{
            background: "#C1F5FF",
            top: "17rem",
            width: "21rem",
            height: "11rem",
            left: "-9rem",
          }}
        ></div>
      </div>
    </div>
  );
};

export default Intro;
