import React, { useContext } from "react";
import "./Intro.css";
import Heading from "../common/heading/Heading";
import Vector1 from "../../img/Vector1.png";
import Vector2 from "../../img/Vector2.png";
import boy from "../../img/boy.svg";
import glassesimoji from "../../img/glassesimoji.png";
import thumbup from "../../img/point-ico.svg";
import star from "../../img/star-ico.svg";
import cursor from "../../img/cursor-ico.svg";
import crown from "../../img/crown.png";
import FloatinDiv from "../FloatingDiv/FloatingDiv";
import Github from "../../img/github.png";
import LinkedIn from "../../img/linkedin.png";
import Instagram from "../../img/instagram.png";
import {constants} from '../../Const';
import { motion } from "framer-motion";
import { Link } from "react-scroll";
 const Intro = (props) => {
  var image1 ="";
  if(props.page.Intro_background_image_1  && props.page.Intro_background_image_1 .data  && props.page.Intro_background_image_1 .data.length>0){
   image1 = constants.link+props.page.Intro_background_image_1 .data[0].attributes.url ;
  }
   var image2 ="";
   if(props.page.Intro_background_image_3 && props.page.Intro_background_image_3.data  && props.page.Intro_background_image_3.data.length>0){
    image2 = constants.link+props.page.Intro_background_image_3.data[0].attributes.url ;
   }
    var image3 ="";
    if(props.page.Intro_background_image_2 && props.page.Intro_background_image_2.data  && props.page.Intro_background_image_2.data.length>0){
     image3 = constants.link+props.page.Intro_background_image_2.data[0].attributes.url ;
    }// Transition
  const transition = { duration: 2, type: "spring" };

  // context

 if(props.page){
  return (
    <div className="Intro" id="Intro">
      {/* left name side */}
      <div className="i-left">
        <div className="i-name">
          {/* yahan change hy darkmode ka */}
          <div className="intro__content">
          <Heading subtitle={props.page.Intro_title} title={props.page.Intro_description} />
              <h4>{props.page.Intro_directory} <i className="fa fa-arrow-right" aria-hidden="true"></i></h4>
            </div>

        </div>
        <Link to="contact" smooth={true} spy={true}>

        </Link>
        {/* social icons */}
        <div className="i-icons">

        </div>
      </div>
      {/* right image side */}
      <div className="i-right">

        <img src={image1} alt="" />
        <img src={image2} alt="" />
        {/* animation */}
        <motion.img
          initial={{ left: "-30%" }}
          whileInView={{ left: "-10%" }}
          className="Crown"
          transition={transition}
          src={image3}
          alt=""
        />



        {/* <motion.div
          initial={{ left: "9rem", top: "18rem" }}
          whileInView={{ left: "0rem" }}
          transition={transition}
          className="floating-div"
        >
          <FloatinDiv img={star}/>
        </motion.div> */}

        <div className="blur" style={{ background: "rgb(238 210 255)" }}></div>
        <div
          className="blur"
          style={{
            background: "#C1F5FF",
            top: "17rem",
            width: "21rem",
            height: "11rem",
            left: "-9rem",
          }}
        ></div>
      </div>
    </div>
  );
        }
};


export default Intro;
