import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { NavBar } from "./components/NavBar";
import { Banner } from "./components/Banner";
import { Skills } from "./components/Skills";
import { Projects } from "./components/Projects";
import { Contact } from "./components/Contact";
import { Footer } from "./components/Footer";
import { Cards } from "./components/Cards" ;
import axios from 'axios';
import { useEffect, useState } from 'react';

function App() {
  const [error, setError] = useState(null);
  const [homePage, setHomePage] = useState({ Description: "", Title: "", SubTitle:"" });

  useEffect(() => {
    console.log(homePage);
    axios
      .get('http://localhost:1337/api/home-page?populate=deep')
      .then(({ data }) => setHomePage(data.data.attributes))
      .catch((error) => setError(error))
  }, [])




  if (error) {
    // Print errors if any
    return <div>An error occured: {error.message}</div>;
  }
  return (
    <div className="App">
      <NavBar />
      <Banner page={homePage.BannerLayout} />
      <Skills page={homePage.clients}/>
      <Cards />
      <Projects />
      <Contact />
      <Footer />
    </div>
  );
}

export default App;
