import React, { useContext } from "react";
import "./Intro.css";
import Vector1 from "../../img/Vector1.png";
import Vector2 from "../../img/Vector2.png";
import boy from "../../img/boy.svg";
import glassesimoji from "../../img/glassesimoji.png";
import thumbup from "../../img/point-ico.svg";
import crown from "../../img/crown.png";
import FloatinDiv from "../FloatingDiv/FloatingDiv";
import Github from "../../img/github.png";
import LinkedIn from "../../img/linkedin.png";
import Instagram from "../../img/instagram.png";

import { motion } from "framer-motion";
import { Link } from "react-scroll";
const Intro = (props) => {
  // Transition
  const transition = { duration: 2, type: "spring" };

  // context
 if(props.page){
  return (
    <div className="Intro" id="Intro">
      {/* left name side */}
      <div className="i-left">
        <div className="i-name">
          {/* yahan change hy darkmode ka */}
          <div className="about__content">
              <h2>{props.page.A_cardTitle}</h2>
              <p>
              {props.page.A_cardDescription}
              </p>
              <h4>{ props.page.A_cardLabel} <i className="fa fa-arrow-right" aria-hidden="true"></i></h4>
            </div>

        </div>
        <Link to="contact" smooth={true} spy={true}>

        </Link>
        {/* social icons */}
        <div className="i-icons">

        </div>
      </div>
      {/* right image side */}
      <div className="i-right">
        <img src={Vector1} alt="" />
        <img src={Vector2} alt="" />
        <img src={boy} alt="" />
        {/* animation */}
        <motion.img
          initial={{ left: "-36%" }}
          whileInView={{ left: "-24%" }}
          className="Crown"
          transition={transition}
          src={crown}
          alt=""
        />

        <motion.div
          initial={{ top: "-4%", left: "74%" }}
          whileInView={{ left: "68%" }}
          transition={transition}
          className="floating-div"
        >
          <FloatinDiv img={crown}/>
        </motion.div>

        {/* animation */}
        <motion.div
          initial={{ left: "9rem", top: "18rem" }}
          whileInView={{ left: "0rem" }}
          transition={transition}
          className="floating-div"
        >
          {/* floatinDiv mein change hy dark mode ka */}
          <FloatinDiv img={thumbup}/>
        </motion.div>

        <div className="blur" style={{ background: "rgb(238 210 255)" }}></div>
        <div
          className="blur"
          style={{
            background: "#C1F5FF",
            top: "17rem",
            width: "21rem",
            height: "11rem",
            left: "-9rem",
          }}
        ></div>
      </div>
    </div>
  );
        }
};

export default Intro;
