import React, { useState } from "react";
import { Container, Row, Col } from "reactstrap";

import cmsImg from "../../assets/img/compdxp.png";
import "./cms.css";

import ReactPlayer from "react-player";

export const Cms = (props) =>  {
  const [showVideo, setShowVideo] = useState(false);
  return (
    <section>
      <Container>
        <Row>
          <Col lg="6" md="6">
            <div className="cms__content">
              <h2>A CMS that fits you, not the other way around</h2>
              <p>
              Whatever tools your team needs - eCommerce, CRM, campaign management, or analytics - you can integrate them with Umbraco.

Thereâ€™s no vendor lock-in. With our composable approach to building digital experience platforms (DXP), you choose top-of-the-class tech solutions and create the exact tech stack you prefer. Build and scale a customized digital experience with a CMS at the core that connects all your tools without friction.
              </p>
              <h4>explore content modeling features <i className="fa fa-arrow-right" aria-hidden="true"></i></h4>
            </div>
          </Col>

          <Col lg="6" md="6">
            <div className="cms__img">
              {showVideo ? (
                <ReactPlayer
                  url="https://www.youtube.com/watch?v=qFp27TR4Yew"
                  controls
                  width="100%"
                  height="350px"
                />
              ) : (
                <img src={cmsImg} alt="" className="w-100" />
              )}

              {!showVideo && (
                <span className="play__icon">
                  <i
                   className='far fa-play-circle'
                    onClick={() => setShowVideo(!showVideo)}
                  ></i>
                </span>
              )}
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};
