import React from "react"
import "./features.css"
import Heading from "../common/heading/Heading"
import {constants} from "../../Const" ; 

export const Features = (props) =>  {
  if(props.page){
    var image1 ="";
    if(props.page.feature_1_image && props.page.feature_1_image.data  && props.page.feature_1_image.data.length>0){
     image1 = constants.link+props.page.feature_1_image.data[0].attributes.url ;
    }
  return (

    <>
      <section className='online'>
        <div className='container'>
          <Heading subtitle={props.features_title} title={props.features_p} />
          <div className='content grid3'>
           
              <div className='box'>
                <div className='img'>
                  <img src={image1} />
                 
                </div>
                <h1 className="CourseName">High Secuirty</h1>
                <p id="textCourse">Member roles and permissions</p>
              </div>
              
          </div>
        </div>
      </section>
    </>
  );
  }
}
