import React, { useRef, useState } from "react"
import { faq } from "../../dummydata"
import Heading from "../common/heading/Heading"

const Faq = () => {
  const [click, setClick] = useState(false)

  const toggle = (index) => {
    if (click === index) {
      return setClick(null)
    }
    setClick(index)
  }

  return (
    <>
      
      <section className='faq'>
        <div className='container'>
        <h1 className="title-faq1"> Frequently  </h1>
        <h1 className="title-faq2">  Asked Question </h1>

        
          {faq.map((val, index) => (
            <div className='box'>
              <button className='accordion' onClick={() => toggle(index)} key={index}>
              
                <h2> <span>{click === index ? <i id="arrow"className='fa fa-chevron-down'></i> : <i className='fa fa-chevron-right'></i>}</span>{  val.title}</h2>
              
              </button>
              {click === index ? (
                <div className='text'>
                  <p>{val.desc}</p>
                </div>
              ) : null}
            </div>
          ))}
        </div>
      </section>
      
       
    </>
  )
}

export default Faq
