import React from "react"
import "./cmsintegrate.css"
import { Container, Row, Col } from "reactstrap";
import Heading from "../common/heading/Heading";
import Image from "../../assets/img/v8_language_variants.png"
import {constants} from "../../Const" ;

export const CmsIntegrate = (props) =>  {
  
  if(props.page){
      return (
        <>
          <section className='online'>
            <Container>
            <Row className="align-items-center">
              <Heading subtitle={props.page.features_title} title={props.page.features_p} />
                  <Col size={9} md={12}>
                  <div className='box'>
                    <div className='img'>
                      <img src={Image} />
                    </div>
                    <h1 className="CourseName">Umbraco’s intuitive UI, great developer experience, and flexibility provide the perfect basis for a truly composable digital experience platform (DXP). Create a tech stack that fits you, not the other way around, and enrich your digital content and online presence.

Whether it’s with custom integrations or the extensive list of pre-built components for a wide range of popular vendors - it can all be done with Umbraco. 

Take a look at Umbraco's Integrations and Extensibility features or visit Umbraco Marketplace for ready-to-use Umbraco integrations</h1>
                    <p>{props.page.feature_1_description}</p>
                  </div>
                  </Col>
        
       
               
                  
                  
              </Row>
            </Container>
          </section>
        </>
        );
  }
}
